@import url("https://fonts.googleapis.com/css2?family=Inconsolata:wght@400;700&display=swap");
@page {
        /* en-US: This attribute establishes the page size in W x H for any browser*/
        /* Size attribute is always width x height, leave single whitespace, and specify units.*/
        /* Letter Landscape: 279mm 216mm*/
        /* US Legal Landscape: 356mm 216mm; */
        /* Guatemalan Oficio Landscape: 330mm 216mm; */
        /* Letter Portrait: 216mm 279mm; */
        /* US Legal Portrait: 216mm 356mm; */
        /* Guatemalan Oficio Portrait: 216mm 330mm; */
        size: 216mm 330mm !important;
        /* Chrome sets own margins, we change these printer settings */
        /* Order: Top, Right, Bottom, Left*/
        margin: 15mm 5mm 15mm 5mm;
        /* Could also use margin-top: 5mm; margin-right: 5mm; margin-bottom: 5mm; margin-left: 5mm; */
    }
    
.mi-texto {
    font-weight: normal;
    font-family: "Inconsolata", monospace;
    font-size: 11px;
    font-weight: 400;
}

.encabezados-tabla th {
    font-family: 'Hind', sans-serif;
    font-weight: 400;
    color: #000000 !important;
    vertical-align: middle !important;
    height: 35px;
    font-size: 14px;
    line-height: 1.2;
    font-weight: unset;
    background: #FFFFFF !important;
}

.borderless {
    border: none;
}

.borderright {
    border-right:1px solid black;
    border-collapse: collapse;
}

.table {
    border:1px solid black;
    border-collapse: separate;
    border-radius:3px;
}

td {
    border-top: none !important; 
    border-bottom: none !important; 
}

.encabezados-style th{
    background: #FFFFFF !important;
    border-bottom: 1px solid black !important; 
}

 #pointer {
  width: 200px;
  height: 40px;
  position: relative;
  background: black;
  border: 1px black;
}
#pointer:after {
  content: "";
  position: absolute;
  left: 0;
  bottom: 0;
  width: 0;
  height: 0;
  border-top: 20px solid transparent;
  border-bottom: 20px solid transparent;
}
#pointer:before {
  content: "";
  position: absolute;
  right: -20px;
  bottom: 0;
  width: 0;
  height: 0;
  border-left: 20px solid black;
  border-top: 20px solid transparent;
  border-bottom: 20px solid transparent;
}

div.main {
  margin-right:30px;
}
ol > li.test {        
  display: table-cell;
  height: 30px;        
  position: relative;
  padding: 0px;
  margin: 0px;
  text-align: center;
  border: 1px solid black;
}
ol > li.test > div {         
  position:relative;        
  line-height: 30px; /* equal to the list item's height */
  height:100%;
  width: 100%;         
}

ol {
  display: table;
  width: 100%;
  padding: 0px;
  margin: 0px;
  position: relative;
}
ol > li.test > div:after, ol > li.test > div:before {
  content:"";
  display:inline-block;        
  border-width: 16px;
  border-style: solid;
  width: 0px;
  height: 0px;
  left: 100%;
  top: -1px;        
  position: absolute;        
  z-index: 1;
}
ol > li.test > div:after, ol  > div:before {
  border-color: transparent transparent transparent black;
}
ol > li.test > div:before {
  border-width: 14px;
  display: block;
  border-color: transparent transparent transparent #ffffff;
  z-index: 2;
  top:1px;
}

.testtwo { 
  border: 1px solid black;
  display: table-cell;
  height: 20px;  
  position: relative;
  padding: 0px;
  margin: 0px;
  text-align: center;
}
